// src/pages/Chatbot.jsx
import React, { useState } from 'react';

function Chatbot() {
  const [message, setMessage] = useState('');  // mensaje que escribe el usuario
  const [chat, setChat] = useState([]);        // historial de chat

  const handleSend = async () => {
    if (!message) return;

    const res = await fetch("http://127.0.0.1:8000/api/chatbot/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ message })
    });
    const data = await res.json();

    // Guardar mensaje del usuario y respuesta del bot
    setChat([...chat, { user: message, bot: data.reply }]);
    setMessage('');  // limpiar input
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>MOGI Chatbot</h1>

      <div style={{ border: '1px solid #ccc', padding: '10px', height: '300px', overflowY: 'scroll', marginBottom: '10px' }}>
        {chat.map((c, i) => (
          <div key={i} style={{ marginBottom: '8px' }}>
            <b>TÃº:</b> {c.user} <br />
            <b>MOGI:</b> {c.bot}
          </div>
        ))}
      </div>

      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Escribe tu mensaje..."
        style={{ width: '70%', padding: '5px' }}
      />
      <button onClick={handleSend} style={{ padding: '5px 10px', marginLeft: '5px' }}>Enviar</button>
    </div>
  );
}

export default Chatbot;
